import React, { Component } from "react";
import { Alert, Fade, Button, Form } from "react-bootstrap";
import "./Login.css";
import FplStatsApi from "../components/FplStatsApi";

export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      entry_id: "",
      player_cookie: "",
      showLoginAlert: false
    };
  }

  setShowLoginAlert(show) {
    this.setState({showLoginAlert: show})
  }

  validateForm() {
    return this.state.entry_id.length > 0 && this.state.player_cookie.length > 0;
  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  handleSubmit = async event => {
    event.preventDefault();
    FplStatsApi.entryData(this.state.entry_id, this.state.player_cookie)
      .then((rsp) => {
        alert(rsp.data.name);
        localStorage.setItem('entry_id', this.state.entry_id);
        this.props.userHasAuthenticated(true);
        this.props.history.push("/");
      })
      .catch(() => {
        this.setShowLoginAlert(true)
      });

  }

  render() {
    return (
      <div className="login">
        {this.state.showLoginAlert &&
        <Fade in={this.state.showLoginAlert}>
          <Alert variant="danger" onClose={() => this.setShowLoginAlert(false)} dismissible>
            <Alert.Heading>Login Failed</Alert.Heading>
            <p>
              Please ensure your entry_id and player_cookie are correct.
              Remember to exclude speech marks from the cookie.
            </p>
          </Alert>
        </Fade>
        }
        <form onSubmit={this.handleSubmit}>
          <Form.Group controlId="entry_id">
            <Form.Label>Entry ID:</Form.Label>
            <Form.Control
              autoFocus
              value={this.state.entry_id}
              onChange={this.handleChange}
            />
          </Form.Group>
          <Form.Group controlId="player_cookie">
            <Form.Label>Player Cookie:</Form.Label>
            <Form.Control
              value={this.state.player_cookie}
              onChange={this.handleChange}
            />
          </Form.Group>
          <Button
            block
            disabled={!this.validateForm()}
            type="submit"
          >
            Login
          </Button>
        </form>
      </div>
    );
  }
}
