import React, { Component, Fragment } from 'react';
import { Link, withRouter } from "react-router-dom";
import { Nav, Navbar, NavItem } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import Routes from './Routes';
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      entryID: "",
      entryCookie: "",
      isLoggedIn: false,
    };
  }
  componentDidMount() {
    this.setEntryID(localStorage.getItem('entryID') || "");
    this.setEntryCookie(localStorage.getItem('entryCookie') || "");
    if (this.props.setEntryID !== "" && this.props.setEntryCookie !== "") {
        this.setLoggedIn(true);
    }
  }
  setEntryID = id => {
    localStorage.setItem('entryID', this.state.entryID);
    this.setState({
      entryID: id,
    });
  }
  setEntryCookie = cookie => {
    localStorage.setItem('entryCookie', this.state.entryCookie);
    this.setState({
      entryCookie: cookie,
    });
  }
  setLoggedIn = loggedIn => {
    this.setState({
      isLoggedIn: loggedIn,
    });
  }
  handleLogout = event => {
    this.setEntryID("");
    this.setEntryCookie("");
    this.setLoggedIn(false);
    this.props.history.push("/login");
  }
  render() {
    const childProps = {
      entryID: this.state.entryID,
      setEntryID: this.setEntryID,
      entryCookie: this.state.entryCookie,
      setEntryCookie: this.setEntryCookie,
      isLoggedIn: this.state.isLoggedIn,
      setLoggedIn: this.setLoggedIn,
    };
    return (
      <div className="app-container">
        <Navbar fluid collapseOnSelect>
          <Navbar.Header>
            <Navbar.Brand>
              <Link to="/">FPL Stats</Link>
            </Navbar.Brand>
            <Navbar.Toggle />
          </Navbar.Header>
          <Navbar.Collapse>
            <Nav pullRight>
              {this.state.isLoggedIn
                ? <Fragment>
                    <Navbar.Text>
                      Entry ID: <b>{this.state.entryID ? this.state.entryID : 'No League Selected'}</b>
                    </Navbar.Text> 
                    <NavItem onClick={this.handleLogout}>Logout</NavItem>
                  </Fragment>
                : <Fragment>
                    <LinkContainer to="/login">
                      <NavItem>Login</NavItem>
                    </LinkContainer>
                  </Fragment>
              }
            </Nav>
          </Navbar.Collapse>
        </Navbar>
        <Routes childProps={childProps} />
      </div>
    );
  }
}

export default withRouter(App);
