import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Navbar } from 'react-bootstrap';
import Routes from './Routes';
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      entryID: "",
      entryCookie: "",
      isLoggedIn: false,
    };
  }
  setEntryID = id => {
    localStorage.setItem('entryID', this.state.entryID);
    this.setState({
      entryID: id,
    });
  }
  setEntryCookie = cookie => {
    localStorage.setItem('entryCookie', this.state.entryCookie);
    this.setState({
      entryCookie: cookie,
    });
  }
  setLoggedIn = loggedIn => {
    this.setState({
      isLoggedIn: loggedIn,
    });
  }
  handleLogout = event => {
    event.preventDefault();
    this.setEntryID("");
    this.setEntryCookie("");
    this.setLoggedIn(false);
    this.props.history.push("/login");
  }
  render() {
    const childProps = {
      entryID: this.state.entryID,
      setEntryID: this.setEntryID,
      entryCookie: this.state.entryCookie,
      setEntryCookie: this.setEntryCookie
    };
    return (
      <div className="app-container">
        <Navbar bg="light" expand="lg">
          <Navbar.Brand>
            <Link to='/home' style={{ textDecoration: 'none' }}>FPL Stats</Link>
          </Navbar.Brand>
          <Navbar.Toggle />
          {this.state.isLoggedIn
            ? <NavItem onClick={this.handleLogout}>Logout</NavItem>
            : <Navbar.Collapse className="justify-content-end">
                <Navbar.Text>
                  Entry ID: <b>{this.state.entryID ? this.state.entryID : 'No League Selected'}</b>
                </Navbar.Text>
              </Navbar.Collapse>
          }
        </Navbar>
        <Routes childProps={childProps}/>
      </div>
    );
  }
}

export default App;
