import React, { Component } from "react";
import { Button, FormGroup, FormControl, FormLabel } from "react-bootstrap";
import "./Login.css";

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state={
            entryID: "",
            entryCookie: "",
        }
    }

    componentDidMount() {
        this.props.setEntryID(localStorage.getItem('entryID') || "");
        this.props.setEntryCookie(localStorage.getItem('entryCookie') || "");
      }

    validateForm() {
        return (this.state.entryID.length > 0 && this.state.entryCookie.length > 0);
    }

    handleChange = event => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }

    handleSubmit = event => {
        event.preventDefault();
        this.props.setEntryID(this.state.entryID);
        localStorage.setItem('entryID', this.state.entryID);
        this.props.history.push("/home");
    }

    render() {
        return (
            <div className="login">
                <form onSubmit={this.handleSubmit}>
                    <FormGroup controlId="entryID" size="large">
                        <FormLabel>Entry ID:</FormLabel>
                        <FormControl
                            autoFocus
                            type="text"
                            value={this.state.entryID}
                            onChange={this.handleChange}
                        />
                    </FormGroup>
                    <FormGroup controlId="entryCookie" size="large">
                        <FormLabel>Entry Cookie:</FormLabel>
                        <FormControl
                            autoFocus
                            type="text"
                            value={this.state.entryCookie}
                            onChange={this.handleChange}
                        />
                    </FormGroup>
                    <Button
                        block
                        size="large"
                        disabled={!this.validateForm()}
                        type="submit"
                    >
                    GO!
                    </Button>
                </form>
            </div>
        );
    }
}
