import React, { Component } from "react";
import { Button, FormGroup, FormControl, FormLabel } from "react-bootstrap";
import "./Login.css";

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state={
            leagueID: "",
        }
    }

    componentDidMount() {
        this.props.setLeagueID(localStorage.getItem('leagueID') || "");
      }

    validateForm() {
        return this.state.leagueID.length > 0;
    }

    handleChange = event => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }

    handleSubmit = event => {
        event.preventDefault();
        this.props.setLeagueID(this.state.leagueID);
        localStorage.setItem('leagueID', this.state.leagueID);
        this.props.history.push("/home");
    }

    render() {
        return (
            <div className="login">
                <form onSubmit={this.handleSubmit}>
                    <FormGroup controlId="leagueID" size="large">
                        <FormLabel>League ID</FormLabel>
                        <FormControl
                            autoFocus
                            type="text"
                            value={this.state.leagueID}
                            onChange={this.handleChange}
                        />
                    </FormGroup>
                    <Button
                        block
                        size="large"
                        disabled={!this.validateForm()}
                        type="submit"
                    >
                    GO!
                    </Button>
                </form>
            </div>
        );
    }
}
