import React, { Component } from "react";
import { Nav, Navbar } from "react-bootstrap";
import "./App.css";
import Routes from "./Routes";
import { withRouter } from "react-router-dom";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isAuthenticated: false,
      isAuthenticating: true,
      player_name: null,
      player_leagues: [],
    };
  }
  async componentDidMount() {
    if (localStorage.getItem('entry_id') && localStorage.getItem('player_cookie')) {
      FplStatsApi.entryData(localStorage.getItem('entry_id'), localStorage.getItem('player_cookie'))
      .then((rsp) => {
        localStorage.setItem('entry_id', this.state.entry_id);
        localStorage.setItem('player_cookie', this.state.player_cookie);
        this.setPlayerData(rsp.data.name, rsp.data.leagues);
        this.userHasAuthenticated(true);
      });
    }
    this.setState({ isAuthenticating: false });
  }
  userHasAuthenticated = authenticated => {
    this.setState({ isAuthenticated: authenticated });
  }
  setPlayerData = (player_name, player_leagues) => {
    this.setState(
      {
        player_name: player_name,
        player_leagues: player_leagues
      }
    )
  }
  handleLogout = async event => {
    localStorage.removeItem('entry_id')
    this.userHasAuthenticated(false);
    this.props.history.push("/login");
  }
  render() {
    const childProps = {
      isAuthenticated: this.state.isAuthenticated,
      player_name: this.state.player_name,
      userHasAuthenticated: this.userHasAuthenticated,
      setPlayerData: this.setPlayerData
    };
    return (
      !this.state.isAuthenticating &&
      <div className="app container">
        <Navbar bg="light">
          <Navbar.Brand href="/">FPL Stats</Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
          <Nav>
            {this.state.isAuthenticated
              ? <Nav.Link onClick={this.handleLogout} className="btn btn-outline-secondary">Logout</Nav.Link>
              : <Nav.Link href="/login" className="btn btn-outline-secondary">Login</Nav.Link>
            }
          </Nav>
        </Navbar.Collapse>
        </Navbar>
        <Routes childProps={childProps} />
      </div>
    );
  }
}

export default withRouter(App);
