import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { Navbar, Nav } from 'react-bootstrap';
import Routes from './Routes';
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      entryID: "",
      entryCookie: "",
      isLoggedIn: false,
    };
  }
  componentDidMount() {
    this.setEntryID(localStorage.getItem('entryID') || "");
    this.setEntryCookie(localStorage.getItem('entryCookie') || "");
    if (this.props.setEntryID != "" && this.props.setEntryCookie != "") {
        this.setLoggedIn(true);
    }
  }
  setEntryID = id => {
    localStorage.setItem('entryID', this.state.entryID);
    this.setState({
      entryID: id,
    });
  }
  setEntryCookie = cookie => {
    localStorage.setItem('entryCookie', this.state.entryCookie);
    this.setState({
      entryCookie: cookie,
    });
  }
  setLoggedIn = loggedIn => {
    this.setState({
      isLoggedIn: loggedIn,
    });
  }
  handleLogout = event => {
    this.setEntryID("");
    this.setEntryCookie("");
    this.setLoggedIn(false);
    this.props.history.push("/login");
  }
  render() {
    const childProps = {
      entryID: this.state.entryID,
      setEntryID: this.setEntryID,
      entryCookie: this.state.entryCookie,
      setEntryCookie: this.setEntryCookie,
      isLoggedIn: this.state.isLoggedIn,
      setLoggedIn: this.setLoggedIn,
    };
    return (
      <div className="app-container">
        <Navbar bg="light" expand="lg">
          <Navbar.Brand>
            <Link to='/home' style={{ textDecoration: 'none' }}>FPL Stats</Link>
          </Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
            <Nav className="mr-auto">
              {this.state.isLoggedIn ? (
                  <Nav.Text>
                    Entry ID: <b>{this.state.entryID ? this.state.entryID : 'No League Selected'}</b>
                  </Nav.Text>
              ) : ( 
                <Nav.Link href="/login">Login</Nav.Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar>
        <Routes childProps={childProps}/>
      </div>
    );
  }
}

export default withRouter(App);
