import React, { Component } from "react";
import { Nav, Navbar, NavDropdown } from "react-bootstrap";
import "./App.css";
import Routes from "./Routes";
import { withRouter } from "react-router-dom";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isAuthenticated: false,
      isAuthenticating: true,
      player_name: null,
      player_leagues: [],
      selected_league: "Select a league"
    };
  }
  async componentDidMount() {
    if (
      localStorage.getItem('entry_id') && 
      localStorage.getItem('player_cookie') && 
      localStorage.getItem('player_name') && 
      localStorage.getItem('player_leagues')
      ) {
        this.setPlayerData(localStorage.getItem('player_name'), JSON.parse(localStorage.getItem('player_leagues')));
        this.userHasAuthenticated(true);
    };
    this.setState({ isAuthenticating: false });
  }
  userHasAuthenticated = authenticated => {
    this.setState({ isAuthenticated: authenticated });
  }
  setPlayerData = (player_name, player_leagues) => {
    this.setState(
      {
        player_name: player_name,
        player_leagues: player_leagues
      }
    )
  }
  handleLogout = async event => {
    localStorage.removeItem('entry_id')
    this.userHasAuthenticated(false);
    this.props.history.push("/login");
  }
  league_selected = (eventKey, event) => {
    this.setState({
      "selected_league": eventKey
    });
  }
  render() {
    const childProps = {
      isAuthenticated: this.state.isAuthenticated,
      player_name: this.state.player_name,
      userHasAuthenticated: this.userHasAuthenticated,
      setPlayerData: this.setPlayerData
    };
    return (
      !this.state.isAuthenticating &&
      <div className="app container">
        <Navbar bg="light">
          <Navbar.Brand href="/">FPL Stats</Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
          <Nav>
            {this.state.isAuthenticated
              ? [
                <NavDropdown key="nav-dropdown" className="mr-4" title={this.state.selected_league} id="basic-nav-dropdown" onSelect={this.league_selected}>
                  {this.state.player_leagues.map((item, index) => (
                    <NavDropdown.Item eventKey={item.name} href="#" key={item.id} value={item.name}>{item.name}</NavDropdown.Item>
                  ))}
                </NavDropdown>,
                <Nav.Link key="login-link" onClick={this.handleLogout} className="btn btn-outline-secondary">Logout</Nav.Link>
              ]
              : <Nav.Link href="/login" className="btn btn-outline-secondary">Login</Nav.Link>
            }
          </Nav>
        </Navbar.Collapse>
        </Navbar>
        <Routes childProps={childProps} />
      </div>
    );
  }
}

export default withRouter(App);
