import React, { Component } from "react";
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import "./Login.css";

export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      entry_id: "",
      player_cookie: ""
    };
  }

  validateForm() {
    return this.state.entry_id.length > 0 && this.state.player_cookie.length > 0;
  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  handleSubmit = event => {
    event.preventDefault();
  }

  render() {
    return (
      <div className="login">
        <form onSubmit={this.handleSubmit}>
          <FormGroup controlId="entry_id" bsSize="large">
            <ControlLabel>Entry ID:</ControlLabel>
            <FormControl
              autoFocus
              value={this.state.entry_id}
              onChange={this.handleChange}
            />
          </FormGroup>
          <FormGroup controlId="player_cookie" bsSize="large">
            <ControlLabel>Player Cookie:</ControlLabel>
            <FormControl
              value={this.state.player_cookie}
              onChange={this.handleChange}
            />
          </FormGroup>
          <Button
            block
            bsSize="large"
            disabled={!this.validateForm()}
            type="submit"
          >
            Login
          </Button>
        </form>
      </div>
    );
  }
}
